fields.default:
{% for idx in range(0, doc.copies) %}
    <table class="label-generator">
        <tbody>
            <tr>
                <td class="col-sm-4" style="float: left;">
                    <img src="/files/zE2dUiD.jpe" style="width: 180px; max-width: 160px;">
                </td>
                <td class="col-sm-8" style="float: right;">
                    <div style="float: left;">
                        {% for field in doc.left_fields %}
                        <div class="field-wrapper">
                            <strong>{{ field.label }}</strong>
                            {% if not field.inline %}<br>{% endif %}
                            <span>{{ field.value }}</span>
                        </div>
                        {% endfor %}
                    </div>

                    <div style="float: right;">
                        <div class="field-wrapper">
                            <strong style="font-size: 15px;">{{ '%03d' % (loop.index) }}</strong>
                            <span></span>
                        </div>

                        {% for field in doc.right_fields %}
                        <div class="field-wrapper">
                            <strong>{{ field.label }}</strong>
                            {% if not field.inline %}<br>{% endif %}
                            <span>{{ field.value }}</span>
                        </div>
                        {% endfor %}
                    </div>
                </td>
            </tr>
        </tbody>
    </table>
        {% if loop.index is sameas 1 %}
           <div class="line-break" style="clear: both;"></div>
        {% else %}
            {% if loop.index is divisibleby(doc.copies_per_page) %}
                <div class="break"></div>
            {% else %}
                <div class="line-break" style="clear: both;"></div>
            {% endif %}
        {% endif %}
{% endfor %}

@media print {
    div.break { page-break-after: always; border: none; }
}

@media (min-width: 100px) {
    .break { margin-bottom: 100px; }
}

div.line-break {
    margin-top: 20px;
    margin-bottom: 20px;

    border: 0;
    border-top: 1px solid #d1d8dd;
}

div.field-wrapper { margin-left: 25px; }
